import React from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import "./Categories.css";

// function getButtonsFromCategories(categories) {}

function Categories({ categoryList }) {
  var array = categoryList;
  let buttons = [];
  categoryList.every((item) => {
    var theButton = (
      <button id="theButton" className="CategoryButtons">
        {item.Name}
      </button>
    );
    return buttons.push(theButton);
  });
  return (
    <div className="Categories">
      <div className="CategoriesTop">
        <p>Kategoriler</p>
      </div>
      <hr></hr>
      <div className="CategoriesBody">{buttons}</div>
    </div>
  );
}

export default Categories;
