import React from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import "./ArticlesCard.css";

function getTagsString(tags) {
  var result = "";
  tags.every(
    (item, index, arr) =>
      (result += item ? "@" + item + (index < arr.length - 1 ? ", " : "") : "")
  );
  return result;
}

function ArticlesCard({ theArticle }) {
  var recordDate = theArticle.RecordDate.toLocaleDateString();
  var daysPassed = 3;
  var daysPassed = new Date() - theArticle.RecordDate;
  daysPassed /= 24 * 3600 * 1000;
  daysPassed = Math.round(daysPassed);
  return (
    <div className="ArticlesCard">
      <div className="ArticlesCardTop">
        <p>{theArticle.Title}</p>
      </div>

      <div className="ArticlesCardBody">
        <div className="ArticlesCardBodyImage">
          <img src={theArticle.Image}></img>
        </div>
        <div className="ArticlesCardBodyText">
          <p className="ArticleInfoParagraph">{theArticle.Info}</p>
        </div>
      </div>

      <div className="ArticlesCardBottom">
        <div className="ArticlesCardBottomDiv1">
          <div className="ArticlesCardBottomImage">
            <img src={theArticle.Author.Image}></img>
          </div>
          <div className="ArticlesCardBottomText">
            <p>
              {theArticle.Author.Name} | {recordDate} ({daysPassed} gün önce)
            </p>
          </div>
        </div>

        <div className="ArticlesCardBottomDiv2">
          <div>
            <p>{getTagsString(theArticle.Tags)}</p>
          </div>
          <div>
            <button>Devamını -{">"}</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ArticlesCard;
